import React, { useState } from 'react';
import { Container, Row, Col, Form, Button} from 'react-bootstrap';
import List from './List'


const Home = () => {
    const [userField, setUserField] = useState({
        firstname: "",
        lastname: "",
        email: ""
    })

    const changeUser = (e) => {
        setUserField({
            ...userField,
            [e.target.name]: e.target.Value
        })
        console.log(userField)
    }
    const onSumbitChange = async (e) => {
        e.preventDefault();
        try {
            const responce = await axios.post('http://localhost:3001/users');
            console.log(responce)
        } catch (err) {
            console.log(err)
        }
    }

    return (
        <Container>
            <div className='w-100 d-flex justify-content-center p-3 fs-3' >React And Express Add Users Basic</div>
            <Row>
                <Col md={4}>
                    <div >
                        <h3>Add Yor Detail</h3>
                        <Form >
                           <Form.Group as={Row} className="mb-3" controlId="formHorizontalEmail" onChange={e => changeUser(e)}>
                              <Form.Label className='mt-3'>
                                 
                              </Form.Label>
                              <Col sm={10}>
                                 <Form.Control type="firstname" placeholder="Fristname" />
                              </Col>
                           </Form.Group>

                           <Form.Group as={Row} className="mb-3" controlId="formHorizontalEmail">
                              <Form.Label >
                                 
                              </Form.Label>
                              <Col sm={10}>
                                 <Form.Control type="lastname" placeholder="Lastname" />
                              </Col>
                           </Form.Group>

                           <Form.Group as={Row} className="mb-3" controlId="formHorizontalEmail">
                              <Form.Label>
                                 
                              </Form.Label>
                              <Col sm={10}>
                                 <Form.Control type="email" placeholder="Email" />
                              </Col>
                           </Form.Group>
                           
                           <Form.Group as={Row} className="mb-3">
                             <Col>
                               <Button type="submit">Add </Button>
                             </Col>
                           </Form.Group>
                        </Form>
                    </div>
                </Col>
                <Col md={8}> 
                    <div>
                        <List />
                    </div>
                </Col>
            </Row>
        </Container>
        
    );
}
export default Home;
